import React, { Component } from "react";



export default class Section1 extends Component {
                 state = {
                   what: 2,
                   winScroll: 0,
                   oppositeOpacity:100,
                   fontSize: 60,
                   fontSize2: 30
                 };




                 componentDidMount() {
                   window.addEventListener("scroll", this.listenToScroll);
                 }
                 componentWillUnmount() {
                   window.removeEventListener("scroll", this.listenToScroll);
                 }
                 listenToScroll = () => {
                   const winScroll =
                     document.body.scrollTop ||
                     document.documentElement.scrollTop;
                     const opacity = 140 - winScroll; 
                     const size = 60 - winScroll;
                     const size2 = 30 - .5*winScroll; 

                     console.log("this is the height" + winScroll);

                     this.setState({winScroll:winScroll})
                     this.setState({oppositeOpacity:opacity})
                     this.setState({fontSize:size})
                     this.setState({fontSize2:size2})
                 };




                 render() {



                   return (
                     <div>
                       console.log({"HELLO"})
                       <section
                         id="hero1"
                         className="hero"
                         loop
                         autoPlay
                         playsInline
                       >
                         <div className="inner">
                           <div className="copy">
                             <div style={{
                                 opacity: this.state.oppositeOpacity + "%"
                               }} 
                               id="display"
                             >
                               <p style={{
                                 fontSize: this.state.fontSize + "px"
                               }} id="name">Matthew Krebs</p>
                               <p></p>
                               <p style={{
                                 fontSize: this.state.fontSize2 + "px"}} id="title">Full Stack Web Developer</p>
                               <br />
                               <div id="newButton">
                                 <a href="#myskills" className="btn-liquid">
                                   <span className="extra">
                                     Click to see my Skills
                                   </span>
                                 </a>
                               </div>
                               <div></div>
                             </div>
                           </div>
                         </div>
                       </section>
                     </div>
                   );
                 }

               }
