import React, { Component } from "react";
import { useScrollPosition } from "@n8tb1t/use-scroll-position";



export default class Section1 extends Component {



                 state = {
                   what: 2,
                 };

                 componentDidMount = () => {
                   // hadling cover parallax
                   window.addEventListener("scroll", this.handleOnScroll);
                 };

                 componentWillUnmount = () => {
                   window.removeEventListener("scroll", this.handleOnScroll);
                 };

                 // handle onScroll event
                 handleOnScroll = () => {
                   console.log(this.coverRef.current.height)
                   const wScroll = window.scrollY;
                   this.coverGradRef.current.style.cssText = `transform: translate(0px , -${
                     wScroll / 40
                   }%)`;
                   console.log(window.scrollY)
                 };

                 render() {
                   return (
                     <div>
                       console.log({"HELLO"})
                       <section
                         id="hero1"
                         className="hero"
                         loop
                         autoPlay
                         playsInline
                       >
                         <div className="inner">
                           <div className="copy">
                             <div id="display">
                               <p id="name">Matthew Krebs</p>
                               <p></p>
                               <p id="title">Full Stack Web Developer</p>
                               <br />
                               <div id="newButton">
                                 <a href="#myskills" className="btn-liquid">
                                   <span className="extra">
                                     Click to see my Skills
                                   </span>
                                 </a>
                               </div>
                               <div></div>
                             </div>
                           </div>
                         </div>
                       </section>
                     </div>
                   );
                 }
               }
