import React, { Component } from "react";
import $ from "jquery";

export default class Section1 extends Component {

    state = {
        scro


    }

componentDidUpdate(){

    var scroll_pos = $(this).scrollTop();
    var percent = scroll_pos / 156.25;
    var fpercent = percent * 0.6;
    var fpercentT = percent * 0.3;
    var fontPercent = 0.6 - fpercent;
    var fontPercentT = 0.3 - fpercentT;
    var opacityPercent = 1 - percent;
    var realfontPercent = fontPercent * 100;
    var realfontPercentT = fontPercentT * 100;
    var realOpacityPercent = opacityPercent * 100;
    var scrollTopp = scroll_pos * 4;
    if (scroll_pos > -1 && scroll_pos < 156.25) {
      // $("#display").css('margin-top', scroll_pos + 'px');

      $("#display").css("opacity", realOpacityPercent + "%");
      $("#name").css("font-size", realfontPercent + "px");
      $("#title").css("font-size", realfontPercentT + "px");
      $("#newButton").css("margin-left", scrollTopp + "px");
      $("#newButton").css("margin-top", scrollTopp + "px");

      console.log("scroll " + scroll_pos);

      $(".nav-link").css("color", "white");
      $(".nav-link").css("background", "rgba(0, 0, 0, 0)");
    }
    if (scroll_pos > 156.25 && scroll_pos < 586.25) {
      $("#display").css("opacity", "0%");
      $(".nav-link").css("color", "white");
      $(".nav-link").css("background", "rgba(0, 0, 0, 0)");
    }
    if (scroll_pos > 586.25) {
      $(".nav-link").css("background", "rgba(0, 0, 0, 1)");
      $(".nav-link").css("color", "gray");
    }

}







  render() {
    return (
      <div>
        <section id="hero1" className="hero" loop autoPlay playsInline>
          <div className="inner">
            <div className="copy">
              <div id="display">
                <p id="name">Matthew Krebs</p>
                <p></p>
                <p id="title">Full Stack Web Developer</p>
                <br />
                <div id="newButton">
                  <a href="#myskills" className="btn-liquid">
                    <span className="extra">Click to see my Skills</span>
                  </a>
                </div>
                <div></div>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}
