import React, { Component } from "react";
import { useRef, useLayoutEffect } from 'react'

const isBrowser = typeof window !== `undefined`;

function getScrollPosition({ element, useWindow }) {
  if (!isBrowser) return { x: 0, y: 0 };

  const target = element ? element.current : document.body;
  const position = target.getBoundingClientRect();
  console.log(position)

  return useWindow
    ? { x: window.scrollX, y: window.scrollY }
    : { x: position.left, y: position.top };

}
console

export function useScrollPosition(effect, deps, element, useWindow, wait) {
  const position = useRef(getScrollPosition({ useWindow }));

  let throttleTimeout = null;

  const callBack = () => {
    const currPos = getScrollPosition({ element, useWindow });
    effect({ prevPos: position.current, currPos });
    position.current = currPos;
    throttleTimeout = null;
    console.log(currPos);
  };

  useLayoutEffect(() => {
    const handleScroll = () => {
      if (wait) {
        if (throttleTimeout === null) {
          throttleTimeout = setTimeout(callBack, wait);
        }
      } else {
        callBack();
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, deps);
  console.log("scroll");
}



export default class Section1 extends Component {
  render() {
    return (
      <div>
        <section id="hero1" className="hero" loop autoPlay playsInline>
          <div className="inner">
            <div className="copy">
              <div id="display">
                <p id="name">Matthew Krebs</p>
                <p></p>
                <p id="title">Full Stack Web Developer</p>
                <br />
                <div id="newButton">
                  <a href="#myskills" className="btn-liquid">
                    <span className="extra">Click to see my Skills</span>
                  </a>
                </div>
                <div></div>
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}
