import React, { Component } from 'react';
import PropTypes from "prop-types";



export class TodoItem extends Component {

    getStyle = () => {
            return {
                backgroundColor: "red",
                textAlign:"center",
                textDecoration: this.props.nameofprop.completed ? "line-through":"none"
            }
         };


         render() {


           return (
             <div style={this.getStyle()}>
               <p>
                 <input type="checkbox" onChange={this.props.markComplete2.bind(this,this.props.nameofprop.id )} /> {" "}

                 {this.props.nameofprop.title}
               </p>
             </div>
           );
         }
       }
TodoItem.propTypes = {
  nameofprop: PropTypes.object.isRequired,
};


export default TodoItem
